        #region Xml Translation
        #region Xml Write
        public static void Write_Xml(
            XElement node,
            IMappingGetter item,
            bool doMasks,
            out Mapping_ErrorMask errorMask,
            Mapping_TranslationMask translationMask,
            string name = null)
        {
            ErrorMaskBuilder errorMaskBuilder = doMasks ? new ErrorMaskBuilder() : null;
            Write_Xml(
                name: name,
                node: node,
                item: item,
                errorMask: errorMaskBuilder,
                translationMask: translationMask?.GetCrystal());
            errorMask = Mapping_ErrorMask.Factory(errorMaskBuilder);
        }

        public static void Write_Xml(
            XElement node,
            IMappingGetter item,
            ErrorMaskBuilder errorMask,
            TranslationCrystal translationMask,
            string name = null)
        {
            var elem = new XElement(name ?? "BethesdaGitSync.Mapping");
            node.Add(elem);
            if (name != null)
            {
                elem.SetAttributeValue("type", "BethesdaGitSync.Mapping");
            }
            WriteToNode_Xml(
                item: item,
                node: elem,
                errorMask: errorMask,
                translationMask: translationMask);
        }
        #endregion

        public static void WriteToNode_Xml(
            this IMappingGetter item,
            XElement node,
            ErrorMaskBuilder errorMask,
            TranslationCrystal translationMask)
        {
            if (item.BinaryPath_IsSet
                && (translationMask?.GetShouldTranslate((int)Mapping_FieldIndex.BinaryPath) ?? true))
            {
                FilePathXmlTranslation.Instance.Write(
                    node: node,
                    name: nameof(item.BinaryPath),
                    item: item.BinaryPath,
                    fieldIndex: (int)Mapping_FieldIndex.BinaryPath,
                    errorMask: errorMask);
            }
            if (item.FolderPath_IsSet
                && (translationMask?.GetShouldTranslate((int)Mapping_FieldIndex.FolderPath) ?? true))
            {
                DirectoryPathXmlTranslation.Instance.Write(
                    node: node,
                    name: nameof(item.FolderPath),
                    item: item.FolderPath,
                    fieldIndex: (int)Mapping_FieldIndex.FolderPath,
                    errorMask: errorMask);
            }
        }

        public static void FillPublic_Xml(
            this Mapping item,
            XElement node,
            ErrorMaskBuilder errorMask,
            TranslationCrystal translationMask)
        {
            try
            {
                foreach (var elem in node.Elements())
                {
                    MappingCommon.FillPublicElement_Xml(
                        item: item,
                        node: elem,
                        name: elem.Name.LocalName,
                        errorMask: errorMask,
                        translationMask: translationMask);
                }
            }
            catch (Exception ex)
            when (errorMask != null)
            {
                errorMask.ReportException(ex);
            }
        }

        public static void FillPublicElement_Xml(
            this Mapping item,
            XElement node,
            string name,
            ErrorMaskBuilder errorMask,
            TranslationCrystal translationMask)
        {
            switch (name)
            {
                case "BinaryPath":
                    try
                    {
                        errorMask?.PushIndex((int)Mapping_FieldIndex.BinaryPath);
                        if (FilePathXmlTranslation.Instance.Parse(
                            node: node,
                            item: out FilePath BinaryPathParse,
                            errorMask: errorMask))
                        {
                            item.BinaryPath = BinaryPathParse;
                        }
                        else
                        {
                            item.BinaryPath = default(FilePath);
                        }
                    }
                    catch (Exception ex)
                    when (errorMask != null)
                    {
                        errorMask.ReportException(ex);
                    }
                    finally
                    {
                        errorMask?.PopIndex();
                    }
                    break;
                case "FolderPath":
                    try
                    {
                        errorMask?.PushIndex((int)Mapping_FieldIndex.FolderPath);
                        if (DirectoryPathXmlTranslation.Instance.Parse(
                            node: node,
                            item: out DirectoryPath FolderPathParse,
                            errorMask: errorMask))
                        {
                            item.FolderPath = FolderPathParse;
                        }
                        else
                        {
                            item.FolderPath = default(DirectoryPath);
                        }
                    }
                    catch (Exception ex)
                    when (errorMask != null)
                    {
                        errorMask.ReportException(ex);
                    }
                    finally
                    {
                        errorMask?.PopIndex();
                    }
                    break;
                default:
                    break;
            }
        }

