                    fg.AppendLine($"BinaryPath => {BinaryPath}");
                }
                if (printMask?.FolderPath ?? true)
                {
                    fg.AppendLine($"FolderPath => {FolderPath}");
                }
            }
            fg.AppendLine("]");
        }
